<script type="text/javascript">		
    $(document).ready(function(){
        $(".popup_video").click(function(){
            $(".popup_window_video").html($(this).parent().find("textarea").val());
            $(".popup_window_video iframe").css("max-width", "100%");
            $(".popup_layer").addClass("popup_show");
            $(".popup_close").addClass("popup_show");
            $(".popup_window_video").addClass("popup_show");
                        
        });
        $(".popup_close, .popup_window_video").click(function(){
            $(".popup_layer").removeClass("popup_show");
            $(".popup_close").removeClass("popup_show");
            $(".popup_window_video").removeClass("popup_show");
			 $(".popup_window_video").html("");
        });
    });
